mapper.select:
select m.MEMBERNO, m.KNAME, m.BIRTHDAY, m.MEMCLSCODE, m.MEMGRADECODE, m.TROOPSCOUTY, m.TROOPLEADERY, m.LEADERINCNT, m.ENTERDATE, m.SCOUTINCNT, m.LIFEMEMBERY, (case when m.EMPLOYEEY = '' then 'N' when m.EMPLOYEEY is null then 'N' when m.EMPLOYEEY = 'N' then 'N' else 'Y' end) as EMPLOYEEY, m.SEX, m.MOBILE, m.EMAIL, m.HPOSTCODE, m.HADDR from member m inner join memclscode mc on m.MEMCLSCODE = mc.MEMCLSCODE
left outer join leader l on l.MEMBERNO = m.MEMBERNO left outer join scout s on s.MEMBERNO = m.MEMBERNO where 1=1
and KNAME LIKE CONCAT('%', #{search_kname}, '%')
and m.MEMBERNO =#{search_memberno}
and m.MEMBERNO =#{search_memberno_se}
and m.BIRTHDAY = #{search_birthday}
and li.idx is not null
and m.TROOPLEADERY = 'Y'
and m.TROOPSCOUTY = 'Y'
and m.MEMCLSCODE = #{search_memclscode}
and m.MEMGRADECODE = #{search_memgradecode}
and m.ENTERDATE between #{search_start} and #{search_end}
and (l.ASSOCIATIONCODE = #{search_associationcode} or s.ASSOCIATIONCODE = #{search_associationcode})
group by m.MEMBERNO
order by cast(m.MEMBERNO as unsigned) asc
order by m.BIRTHDAY asc
order by m.KNAME asc

mapper.update:
update member set MEMCLSCODE= #{MEMCLSCODE}, MEMGRADECODE =#{MEMGRADECODE}, BIRTHDAY=#{BIRTHDAY}, KNAME=#{KNAME}, ENAME=#{ENAME}, SEX=#{SEX}, MOBILE=#{MOBILE}, EMAIL=#{EMAIL},
SMSYN=#{SMSYN}, EMAILYN=#{EMAILYN}, JOBCODE=#{JOBCODE}, HPOSTCODE= #{HPOSTCODE}, HADDR=#{HADDR}, UPDATEDATE = now(), EMPLOYEEY = #{EMPLOYEEY}, HTELNO =#{HTELNO},
LEADERSCORE =#{LEADERSCORE}, SCOUTSCHOOLYEAR =#{SCOUTSCHOOLYEAR}, SCOUTSCHOOLBAN =#{SCOUTSCHOOLBAN}, TROOPSCOUTY=#{TROOPSCOUTY}, TROOPLEADERY=#{TROOPLEADERY},
BIGO=#{BIGO}, LIFEMEMBERY=#{LIFEMEMBERY}, LIFERANK= #{LIFERANK}, LIFEMEMBERNO = #{LIFEMEMBERNO}, LIFEENTERDATE=#{LIFEENTERDATE}, LIFESTATUS=#{LIFESTATUS}
where MEMBERNO = #{MEMBERNO}

mapper.select:
select * from member where 1=1 

	and MEMBERNO = #{MEMBERNO}


	and KNAME = #{KNAME}

select * from scoutpositioncode
where USEYN = 'Y' and SCOUTCLSCODE = #{SCOUTCLSCODE}
order by cast(ORD as unsigned)

		select a.ASSOCIATIONNAME
		, (select ORGCLSNAME from orgclscode oc where ORGCLSCODE1 = o.ORGCLSCODE1 limit 1) as ORGCLSNAME1
		, (select ORGCLSNAME from orgclscode oc where ORGCLSCODE1 = o.ORGCLSCODE1 and ORGCLSCODE2 = o.ORGCLSCODE2  limit 1) as ORGCLSNAME2
        , ifnull((select ORGNAME from org o2 where o.PARENT_ORGNO = o2.ORGNO), '') as PARENTORGNAME
		 , o.* 
		from org o
		left outer join associationcode a on a.ASSOCIATIONCODE = o.ASSOCIATIONCODE
		where 1=1


			and o.ASSOCIATIONCODE = #{ASSOCIATIONCODE} 


			and o.ORGCLSCODE1 = #{ORGCLSCODE1}


			and o.ORGCLSCODE2 = #{ORGCLSCODE2}


			and o.PARENT_ORGNO = #{PARENTORGNO}


			and (
				o.ORGNAME LIKE CONCAT('%', #{search_orgtext}, '%')
			) 

		order by ORGCLSCODE1 desc, ORGNAME asc;

mapper.insert:

        select max(cast(MEMBERNO as unsigned)) MEMBERNO from member

	insert into member (MEMBERNO, MEMCLSCODE, MEMGRADECODE, BIRTHDAY, EMPLOYEEY, KNAME, ENAME, SEX, HTELNO, MOBILE, EMAIL, SMSYN, EMAILYN, JOBCODE, HPOSTCODE, HADDR, ENTERDATE, TROOPSCOUTY, TROOPLEADERY, LIFEMEMBERY)
	values(
		(select max(cast(MEMBERNO as unsigned)) + 1 from member t), #{MEMCLSCODE}, #{MEMGRADECODE}, #{BIRTHDAY}, #{EMPLOYEEY}, #{KNAME}, #{ENAME}, #{SEX}, #{HTELNO}, #{MOBILE}, #{EMAIL}, #{SMSYN}, #{EMAILYN}, #{JOBCODE}, #{HPOSTCODE}, #{HADDR}, now(), #{TROOPSCOUTY},#{TROOPLEADERY},#{LIFEMEMBERY}
	)

mapper.select:
		select 
			(select TROOPNAME from troop t1 where t1.TROOPNO = t.PARENT_TROOPNO limit 1) as PARENTTROOPNAME, 
		    sc.SCOUTCLSNAME,
		    (select TROOPCLSNAME from troopclscode tc where tc.TROOPCLSCODE1 = t.TROOPCLSCODE1 and tc.TROOPCLSCODE2 = t.TROOPCLSCODE2) as TROOPCLSNAME,
			t.*
		from troop t
		inner join associationcode a on t.ASSOCIATIONCODE = a.ASSOCIATIONCODE
        left outer join scoutclscode sc on sc.SCOUTCLSCODE = t.SCOUTCLSCODE
        left outer join org o on o.ORGNO = t.ORGNO
        where 1=1

        	and t.ASSOCIATIONCODE = #{ASSOCIATIONCODE} 


        	and t.PARENT_TROOPNO = #{PARENTTROOPNO}


        	 and t.TROOPLEVELCODE = #{TROOPLEVELCODE}


        	and t.TROOPCLSCODE1 = #{TROOPCLSCODE1}


        	and t.TROOPCLSCODE2 = #{TROOPCLSCODE2}


        	and t.SCOUTCLSCODE = #{SCOUTCLSCODE}


        	and o.PARENT_ORGNO = #{PARENTORGNO}



	        	and t.DISPTROOPNO = #{search_text}


				and (
					t.TROOPNAME LIKE CONCAT('%', #{search_text}, '%')
				) 



        #and t.PARENT_TROOPNO = 9689 and t.ORGNO is not null and t.ORGNO != ''


        	order by cast(t.PARENT_TROOPNO as unsigned), length(t.DISPTROOPNO), cast(t.DISPTROOPNO as unsigned)


        	order by t.TROOPNAME asc

WITH LeaderCount AS ( SELECT l.ASSOCIATIONCODE, l.TROOPNO, SUM(CASE WHEN left(l.STARTDAY, 4) = #{search_year} THEN 1 ELSE 0 END) AS LeaderCount, SUM(CASE WHEN left(l.STARTDAY, 4) = #{pre_year} THEN 1 ELSE 0 END) AS PLeaderCount, SUM(CASE WHEN l.PAYY = 'Y' and left(l.STARTDAY, 4) = #{search_year} THEN 1 ELSE 0 END) AS LeaderPayCount FROM leader l LEFT JOIN member m ON m.MEMBERNO = l.MEMBERNO WHERE left(l.STARTDAY, 4) IN (#{search_year}, #{pre_year}) and l.ASSOCIATIONCODE = #{search_associationcode} GROUP BY l.ASSOCIATIONCODE, l.TROOPNO ), ScoutCount AS ( SELECT s.ASSOCIATIONCODE, s.TROOPNO, SUM(CASE WHEN left(s.STARTDAY, 4) = #{search_year} THEN 1 ELSE 0 END) AS ScoutCount, SUM(CASE WHEN left(s.STARTDAY, 4) = #{pre_year} THEN 1 ELSE 0 END) AS PScoutCount, SUM(CASE WHEN s.PAYY = 'Y' and left(s.STARTDAY, 4) = #{search_year} THEN 1 ELSE 0 END) AS ScoutPayCount FROM scout s LEFT JOIN member m ON m.MEMBERNO = s.MEMBERNO WHERE left(s.STARTDAY, 4) IN (#{search_year}, #{pre_year}) and s.ASSOCIATIONCODE = #{search_associationcode} GROUP BY s.ASSOCIATIONCODE, s.TROOPNO ), LeaderGroup AS ( SELECT l.ASSOCIATIONCODE, l.TROOPNO, group_concat(l.MEMBERNO) as LeaderGroupNo, group_concat(m.KNAME) as LeaderGroupName FROM leader l LEFT JOIN member m ON m.MEMBERNO = l.MEMBERNO WHERE left(l.STARTDAY, 4) IN (#{search_year}) and l.ASSOCIATIONCODE = #{ASSOCIATIONCODE} GROUP BY l.ASSOCIATIONCODE, l.TROOPNO ), ScoutGroup AS ( SELECT s.ASSOCIATIONCODE, s.TROOPNO, group_concat(s.MEMBERNO) as ScoutGroupNo, group_concat(m.KNAME) as ScoutGroupName FROM scout s LEFT JOIN member m ON m.MEMBERNO = s.MEMBERNO WHERE left(s.STARTDAY, 4) IN (#{search_year}) and s.ASSOCIATIONCODE = #{ASSOCIATIONCODE} GROUP BY s.ASSOCIATIONCODE, s.TROOPNO ) select t.TROOPNO, ifnull((select TROOPNAME from troop t1 where t1.TROOPNO = t.PARENT_TROOPNO), '' ) as PARENTTROOPNAME, t.PARENT_TROOPNO, t.DISPTROOPNO, t.TROOPNAME, sc.SCOUTCLSNAME, (select TROOPCLSNAME from troopclscode tc where tc.TROOPCLSCODE1 = t.TROOPCLSCODE1 and tc.TROOPCLSCODE2 = t.TROOPCLSCODE2) as TROOPCLSNAME, '일반' as DETAIL, t.LASTREGYEAR , COALESCE(lc.PLeaderCount, 0) AS preleader , COALESCE(sc2.PScoutCount, 0) AS prescout , COALESCE(lc.LeaderCount, 0) AS thisleader , COALESCE(sc2.ScoutCount, 0) AS thisscout , COALESCE(lc.LeaderPayCount, 0) AS payleader , COALESCE(sc2.ScoutPayCount, 0) AS payscout , lg.LeaderGroupNo , lg.LeaderGroupName , sg.ScoutGroupNo , sg.ScoutGroupName
from troop t left outer join associationcode a on t.ASSOCIATIONCODE = a.ASSOCIATIONCODE left outer join scoutclscode sc on sc.SCOUTCLSCODE = t.SCOUTCLSCODE LEFT JOIN LeaderCount lc ON lc.ASSOCIATIONCODE = t.ASSOCIATIONCODE AND lc.TROOPNO = t.TROOPNO LEFT JOIN ScoutCount sc2 ON sc2.ASSOCIATIONCODE = t.ASSOCIATIONCODE AND sc2.TROOPNO = t.TROOPNO LEFT JOIN LeaderGroup lg ON lg.ASSOCIATIONCODE = t.ASSOCIATIONCODE AND lg.TROOPNO = t.TROOPNO LEFT JOIN ScoutGroup sg ON sg.ASSOCIATIONCODE = t.ASSOCIATIONCODE AND sg.TROOPNO = t.TROOPNO
where 1=1
and t.ASSOCIATIONCODE = #{search_associationcode}
and t.PARENT_TROOPNO = #{search_parenttroopno}
and t.TROOPCLSCODE1 = #{search_troopcls}
and t.TROOPCLSCODE2 = #{search_troopcls2}
and t.SCOUTCLSCODE = #{search_level}
and t.DISPTROOPNO = #{search_text2}
and ( t.TROOPNAME LIKE CONCAT('%', #{search_text2}, '%') )
and ( lg.LeaderGroupNo LIKE CONCAT('%', #{search_text}, '%') or sg.ScoutGroupNo LIKE CONCAT('%', #{search_text}, '%') )
and ( lg.LeaderGroupName LIKE CONCAT('%', #{search_text}, '%') or sg.ScoutGroupName LIKE CONCAT('%', #{search_text}, '%') )
order by cast(t.PARENT_TROOPNO as unsigned), length(t.DISPTROOPNO), cast(t.DISPTROOPNO as unsigned)
order by t.TROOPNAME asc

select t.TROOPNO, t.DISPTROOPNO, ifnull((select TROOPNAME from troop t1 where t1.TROOPNO = t.PARENT_TROOPNO), '' ) as PARENTTROOPNAME, t.PARENT_TROOPNO, t.DISPTROOPNO, t.TROOPNAME, sc.SCOUTCLSNAME, (select TROOPCLSNAME from troopclscode tc where tc.TROOPCLSCODE1 = t.TROOPCLSCODE1 and tc.TROOPCLSCODE2 = t.TROOPCLSCODE2) as TROOPCLSNAME, '일반' as DETAIL, t.LASTREGYEAR from troop t left outer join associationcode a on t.ASSOCIATIONCODE = a.ASSOCIATIONCODE left outer join scoutclscode sc on sc.SCOUTCLSCODE = t.SCOUTCLSCODE
where 1=1
and t.ASSOCIATIONCODE = #{search_associationcode}
and t.PARENT_TROOPNO = #{search_parenttroopno}
and t.TROOPCLSCODE1 = #{search_troopcls}
and t.TROOPCLSCODE2 = #{search_troopcls2}
and t.SCOUTCLSCODE = #{search_level}
order by cast(t.PARENT_TROOPNO as unsigned), length(t.DISPTROOPNO), cast(t.DISPTROOPNO as unsigned)
order by cast(t.PARENT_TROOPNO as unsigned), length(t.DISPTROOPNO), cast(t.DISPTROOPNO as unsigned)
order by t.TROOPNAME asc

select t.TROOPNO, a.ASSOCIATIONNAME, (select TROOPNAME from troop t1 where t1.TROOPNO = t.PARENT_TROOPNO limit 1) as PARENTTROOPNAME, t.DISPTROOPNO, t.TROOPNAME
from troop t
left outer join associationcode a on t.ASSOCIATIONCODE = a.ASSOCIATIONCODE
where 1=1

	and a.ASSOCIATIONCODE = #{ASSOCIATIONCODE}


	and t.PARENT_TROOPNO = #{PARENTTROOPNO}


	and t.TROOPCLSCODE1 = #{TROOPCLSCODE1}


	and t.TROOPCLSCODE2 = #{TROOPCLSCODE2}


	and t.TROOPNAME LIKE CONCAT('%', #{TROOPNAME}, '%')

select p.* , a.ASSOCIATIONNAME
from association_price p
left outer join associationcode a on p.ASSOCIATIONCODE = a.ASSOCIATIONCODE
where p.ASSOCIATIONCODE = #{ASSOCIATIONCODE}
and p.YEAR = #{search_year}

select c.ASSOCIATIONCODE, c.title, b.* from board_table b left outer join board_category c on b.BRDNO = c.boardcategory where 1=1
and b.BRDNO = #{BRDNO}
and (USEAT = 'Y')
and (USEAT = 'Y' or USEAT = 'H')
and c.ASSOCIATIONCODE = #{ASSOCIATIONCODE}
and c.ASSOCIATIONCODE != '200'
and SUBJECT LIKE CONCAT('%', #{search_text}, '%')
order by NOTI desc, ENTERDATE desc LIMIT #{limit} OFFSET #{startNumber}

mapper.update:
update board_table
set 
USEAT = #{USEAT}
where BRDCTSNO = #{BRDCTSNO}

mapper.select:
select * from reservation_table 
where 1=1

	and (
		company LIKE CONCAT('%', #{search_text}, '%') or
		depart LIKE CONCAT('%', #{search_text}, '%') or
		name LIKE CONCAT('%', #{search_text}, '%')
	)


	and status = #{search_status}

order by ENTERDATE desc
LIMIT #{limit} OFFSET #{startNumber}

select l.idx, l.name, l.EMAIL, l.MOBILE, l.status, l.ENTERDATE, (case when l.age = '1' then '비버스카우트' when l.age = '2' then '컵스카우트' when l.age = '3' then '스카우트' when l.age = '4' then '벤처스카우트' when l.age = '5' then '지도자' end) as age, (case when l.relation = '1' then '본인' when l.relation = '2' then '부모' when l.relation = '3' then '스카우트 지도자' end) as relation, a.ASSOCIATIONNAME from local_apply l left outer join associationcode a on l.association = a.ASSOCIATIONCODE where 1=1
and ( l.name LIKE CONCAT('%', #{search_text}, '%') or l.mobile LIKE CONCAT('%', #{search_text}, '%') )
and status = #{search_status}
and l.association = #{search_associationcode}
order by ENTERDATE desc

select count(*)
from local_apply l
where 1=1

	and (
		l.name LIKE CONCAT('%', #{search_text}, '%') or
		l.mobile LIKE CONCAT('%', #{search_text}, '%')
	)


	and status = #{search_status}


	and l.association = #{search_associationcode}

select * from youth_table where 1=1
and YEAR = #{YEAR}
order by YEAR asc

select * from popup_table
where 1=1
and idx = #{idx}

